import React from  'react' ;
import './SortingVisualizer.css';

export default class SortingVisualizer extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            array:[] ,
        };
    }
    componentDidMount(){
        this.resetArray();
    }
    resetArray(){
        const array = [] ;
        for(let i = 0 ; i < 400 ; i ++ ){
            array.push(Random_numbers(5 , 725 ) ) ;
        }
        this.setState({array});
    }
    render(){
        const {array} = this.state ;
        return(
            <div className = "bar_container">
            {array.map((value,idx)=>(
                <div className= "bar" key = {idx}
                style = {{height:`${value}px`}}></div>
            ))}
            <div>
            <button onClick={() => this.resetArray() } className = "btn">Generate a new array  </button>
            <button  className = "btn">Merge Sort</button>
            <button  className = "btn">Quick Sort </button>
            <button  className = "btn">Insertion Sort </button>
            <button  className = "btn">Bubble Sort</button>
            </div>
            </div>
        );
    }
    }
function Random_numbers(min , max ){
    return Math.floor(Math.random() * (max - min + 1 ) + min ) ;
}
