import React from  'react' ;
import { mergeSort } from '../sortingalgorithms/sortingalgorithms';
import './SortingVisualizer.css';

export default class SortingVisualizer extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            array:[] ,
        };
    }
    componentDidMount(){
        this.resetArray();
    }
    resetArray(){
        const array = [] ;
        for(let i = 0 ; i < 400 ; i ++ ){
            array.push(Random_numbers(5 , 725 ) ) ;
        }
        this.setState({array});
    }
    mergesort(){
        const javasortedarray = this.state.array.slice()
        .sort((a,b) => a - b );
        const sorted_one = mergeSort(this.state.array);
    }
    quicksort(){}
    insertionsort(){}
    bubblesort(){}
    heapsort(){}
    heapsort(){}
    render(){
        const {array} = this.state ;
        return(
            <div className = "bar_container">
            {array.map((value,idx)=>(
                <div className= "bar" key = {idx}
                style = {{height:`${value}px`}}></div>
            ))}
            <div>
            <button onClick={() => this.resetArray() } className = "btn">Generate a new array  </button>
            <button  onClick={() => this.mergesort() } className = "btn">Merge Sort</button>
            <button  onClick={() => this.quicksort() } className = "btn">Quick Sort </button>
            <button  onClick={() => this.insertionsort() } className = "btn">Insertion Sort </button>
            <button  onClick={() => this.bubblesort() } className = "btn">Bubble Sort</button>
            <button  onClick={() => this.heapsort() } className = "btn">Heap Sort</button>
            </div>
            </div>
        );
    }
    }
function Random_numbers(min , max ){
    return Math.floor(Math.random() * (max - min + 1 ) + min ) ;
}
function AreEqual(arr1 , arr2 ){
    if(arr1.length !== arr2.length ) return false;
    for(let i = 0 ; i < arr1.length ; i ++ ){

        if(arr1[i] != arr2[i] ) return false ;
    }
    return true ;
}
